import { Action } from './ActionsView';
export interface ContextViewModel {
    icon?: string;
    text: string;
}
export interface IconWithTooltip {
    url: string;
    tooltip?: string;
}
export interface TextWithTooltip {
    text: string;
    tooltip?: string;
}
export interface UserViewModel {
    icon?: string;
    name?: string;
}
export interface BadgeViewModel {
    value: number;
    max?: number;
    appearance?: 'default' | 'primary' | 'important' | 'added' | 'removed';
}
export interface LozengeViewModel {
    text: string;
    appearance?: 'default' | 'success' | 'removed' | 'inprogress' | 'new' | 'moved';
    isBold?: boolean;
}
export interface DetailViewModel {
    title?: string;
    icon?: string;
    badge?: BadgeViewModel;
    lozenge?: LozengeViewModel;
    text?: string;
    tooltip?: string;
}
export default interface ViewModel {
    context?: ContextViewModel;
    link?: string;
    icon?: IconWithTooltip;
    user?: UserViewModel;
    preview?: string;
    title?: TextWithTooltip;
    description?: TextWithTooltip;
    details?: DetailViewModel[];
    users?: UserViewModel[];
    actions?: Action[];
    onClick?: () => void;
}
